n="right top" rt_bg_attachment="scroll" rt_overlap="" rt_padding_top="60" rt_padding_bottom="60" rt_row_borders="" rt_grid=""][vc_column width="1/1"][rt_heading size="h3" style="style-4" punchline="Testimonials"]What They Are Saying[/rt_heading][testimonial_carousel list_layout="1/1" align="center" max_item="10" nav="false" dots="true" list_orderby="date" list_order="DESC" client_images="false" style="center big" ids="2353,1735"][/vc_column][/vc_row]', 'ABOUT US', '', 'inherit', 'open', 'open', '', '9-revision-v1', '', '', '2015-09-25 17:39:20', '2015-09-25 17:39:20', '', 9, 'http://www.danielhartdayo.com/2015/09/25/9-revision-v1/', 0, 'revision', '', 0),
